class p_info3011:
    def __init__(self):
        pass

    def process(self, kwargs):
        scode,sdate,edate,maxid = None,None,None,None
        if kwargs.has_key("scode"):
            scode = kwargs["scode"]
        if kwargs.has_key("sdate"):
            sdate = kwargs["sdate"]
        if kwargs.has_key("edate"):
            edate = kwargs["edate"]
            if len(edate) == 8:
                edate = edate[0:4]+'-'+edate[4:6]+'-'+edate[6:8]
            else :
                edate = edate.replace('/','-')            
        if kwargs.has_key("maxid"):
            maxid = int(kwargs["maxid"])

        dtsrc = DataSrc()
        sqlstr = " select OBJECTID,TEXTID,RECID,SECCODE,SECNAME,F001D,F002V,F003V,F004V,F005N,F006V,F007V,F008V,F009V,F010V from INFO3011  where ISVALID=1    "
        if scode is  None:
            #为空时取结束日期当天的所有公告 edate不能为空
            sqlstr = sqlstr + " AND F001D >= '%s 00:00:00'  and  F001D<= '%s 23:59:59' "%(edate,edate)
        else:
            sqlstr = sqlstr + " AND SECCODE ='%s' "%scode

            if sdate is not None and edate is None:
                sqlstr = sqlstr + " AND F001D >='%s' "%sdate
            elif sdate is  None and edate is not None:
                sqlstr = sqlstr + " AND F001D <='%s 23:59:59' "%edate
            elif sdate is  not None and edate is not None:
                sqlstr = sqlstr + " AND F001D <='%s 23:59:59' AND F001D >='%s' "%(edate,sdate)

        if maxid is not None:
            sqlstr = sqlstr + " and  OBJECTID > %s "%maxid

        sqlstr = sqlstr + " order by OBJECTID asc ";

        dtset = dtsrc.readdb(sqlstr,'APIDB')
        return dtset

