        if type == 1:
            for item in vdict.keys():
                valdt = ' ( '
                if sdate is not None and edate is not None :
                    valdt = valdt +  vdict[item] + " >='%s' "%sdate
                    valdt = valdt + " and " + vdict[item] + " <='%s' "%edate
