                     else 1 end type,GREATEST(F003D,F006D,F011D,F012D) MAXDATE,SECCODE
                 FROM (SELECT SECCODE,IFNULL(MAX(F003D),DATE('1949-10-1'))F003D,IFNULL(MAX(F006D),DATE('1949-10-1'))F006D,IFNULL(MAX(F011D),DATE('1949-10-1'))F011D,IFNULL(MAX(F012D),DATE('1949-10-1'))F012D
                           FROM NEEQ6014 where 1 = 1 """
                #增加过滤筛选条件
                sqlstr2 = sqlstr2 + filtedate + " group by SECCODE)e "
                fullstr = " select f.* from( " + sqlstr1 + " )f , " + " ( " + sqlstr2 + " )g  where 1=1 and  f.SECCODE = g.SECCODE "
                fullstr = fullstr +  "  and  ( (g.type = 2 and  f.F003D = g.MAXDATE) or (g.type = 3 and  f.F006D = g.MAXDATE) or (g.type = 4 and  f.F011D = g.MAXDATE) or (g.type = 5 and  f.F012D = g.MAXDATE))"
            elif state == 1:
                fullstr = sqlstr1 
