from dataset import DataSet
from datasrc import DataSrc
import datetime


# 2.61	Meeting Dates
class p_ENF7202:
    def __init__(self):
        pass

    def process(self, kwargs):
        fcode, ptype, unheld,sdate,edate = None, None, None, None, None
        if kwargs.has_key("fcode"):
            fcode = "'" + kwargs["fcode"].replace(",", "','") + "'"
        if kwargs.has_key("sdate"):
            sdate = kwargs["sdate"]
        if kwargs.has_key("edate"):
            edate = kwargs["edate"]
        if kwargs.has_key("ptype"):
            ptype = kwargs["ptype"]
        if kwargs.has_key("unheld"):
            unheld = kwargs["unheld"]

        dset = DataSet()
        if fcode is None and sdate is None and edate is None and unheld is None :
            dset.set_msg(300, "fcode, sdate and edate, and unheld shall not be null simultaneously.", "")
            return dset
        if sdate is not None and edate is not None:
            delta = datetime.datetime.strptime(edate.replace('-', '').replace('/', ''),
                                               '%Y%m%d') - datetime.datetime.strptime(
                sdate.replace('-', '').replace('/', ''), '%Y%m%d')
            if delta.days > 365:
                dset.set_msg(300, "The span between sdate and edate shall not be over 1 year", "")
                return dset

        dtsrc = DataSrc()

        sqlstr = '''SELECT FUNDCODE,FUNDABBR,FUNDNAME,FUNDTYPE,DECLAREDATE,F001V,F002V,F003V,F004V,F005D,F006D,F007D,F008D,F009D From ENF7202 where 1=1 '''

        if fcode is not None:
            sqlstr = sqlstr + " AND FUNDCODE in(%s) " % fcode

        if unheld is not None:
            sqlstr = sqlstr + " AND DATE(F004V)>DATE(NOW()) "

        if ptype is not None and ptype=='2':
            if sdate is not None and edate is not None:
                sqlstr = sqlstr + "and  (F009D>='%s') and  (F009D<='%s') " % (sdate, edate)
            if sdate is not None and edate is None:
                sqlstr = sqlstr + " and  (F009D>='%s') and (F009D<=DATE_ADD('%s',INTERVAL 1 YEAR) ) " % (
                    sdate, sdate)
            if sdate is None and edate is not None:
                sqlstr = sqlstr + " and  (F009D<='%s' ) and (F009D>=DATE_ADD('%s',INTERVAL -1 YEAR) ) " % (
                    edate, edate)
        else:
            if sdate is not None and edate is not None:
                sqlstr = sqlstr + "and  (DECLAREDATE>='%s') and  (DECLAREDATE<='%s') " % (sdate, edate)
            if sdate is not None and edate is None:
                sqlstr = sqlstr + " and  (DECLAREDATE>='%s') and (DECLAREDATE<=DATE_ADD('%s',INTERVAL 1 YEAR) ) " % (
                sdate, sdate)
            if sdate is None and edate is not None:
                sqlstr = sqlstr + " and  (DECLAREDATE<='%s' ) and (DECLAREDATE>=DATE_ADD('%s',INTERVAL -1 YEAR) ) " % (
                edate, edate)

        dtset = dtsrc.readdb(sqlstr, 'APIDB')
        return dtset

